<template>
	<transition name="toast-fade">
		<div class="toast" v-show="showFlag">
	        <i :class="'icon-' + type"></i>
	        <p class="toast-text">{{tip}}</p>
	    </div>
	</transition>
</template>

<script>
	export default {
		props: {
			type: {
				type: String,
				default: 'loading' // loading or success or error or msg
			},
			tip: {
				type: String,
				default: '正在加载中'
			},
			time: {
				type: Number,
				default: 0
			}
		},
		data () {
			return {
				showFlag: false
			}
		},
		methods: {
			show () {
				this.showFlag = true
				if (this.time) {
					setTimeout(() => {
						this.hide()
					}, this.time)
				}
			},
			hide () {
				this.showFlag = false
				this.$emit('done')
			}
		}
	}
</script>

